import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Html extends Component {
  render() {
    const { head = {}, content = '' } = this.props;
    return (
      <html>
        <head>
          {head.title.toComponent()}
          <meta name="viewport" content="width=device-width, initial-scale=1, minimum-scale=1, maximum-scale=1" />
        </head>
        <body>
          <div id="application" dangerouslySetInnerHTML={{ __html: content }} />
          <script src="//cdnjs.cloudflare.com/ajax/libs/react/15.6.1/react.min.js" />
          <script src="//cdnjs.cloudflare.com/ajax/libs/react/15.6.1/react-dom.min.js" />
          <script src="/assets/scripts/client.js" />
          <script src="/assets/lib/react-stage.min.js" />
        </body>
      </html>
    );
  }
}

Html.propTypes = {
  head: PropTypes.object,
  content: PropTypes.string
};

export default Html;
