export default (element) => {
  const tabbableElements = [
    'a[href]',
    'link[href]',
    'button',
    'input:not([type="hidden"])',
    'select',
    'textarea',
    '[tabIndex]:not([tabIndex="-1"])'
  ];
  return Array.prototype.slice.call(element.querySelectorAll(tabbableElements.join(', ').trim()));
}
